// Rename view, save the new name by clicking outside, save the new name by pressing ENTER --- start
$('#rename-view-btn').click(function(){
	$('#view-title').hide();
	$('.view-input').show();
	$('.view-input').select();
	$('.dropdown-menu-more').hide();
	$('.dropdown-menu-more').removeClass('visible-test');
});

$('.view-input').blur(function() {
	if ($.trim(this.value) == ''){
		this.value = (this.defaultValue ? this.defaultValue : '');
	}
	else{
		$('#view-title').html(this.value);
		$('#view-sub-title').html(this.value);
	}
	$('.view-input').hide();
	$('#view-title').show();
});

$('.view-input').keypress(function(event) {
  if (event.keyCode == '13') {
	  if ($.trim(this.value) == ''){
		 this.value = (this.defaultValue ? this.defaultValue : '');
	 }
	 else
	 {
		 $('#view-title').html(this.value);
		 $('#view-sub-title').html(this.value);
	 }

	 $('.view-input').hide();
	 $('#view-title').show();
  }
});
